import useInput from "./Hooks/use-input";


const isNotEmpty =value=>value.trim!==''
const isEmail = value => value.includes('@')


const BasicForm = (props) => {

  const{
    value: firstNameValue,
    isValid: firstNameIsValid,
    valueChangeHandler: firstNameChangedHandler,
    inputBlurHandler: firstNameBlurHandler,
     reset: resetFirstName
       } =  useInput(isNotEmpty)
  const{
    value: lastNameValue,
    isValid: lastNameIsValid,
    valueChangeHandler: LastnameChangedHandler,
    inputBlurHandler: LastNameBlurHandler,
    reset: resetLastName
       } =useInput(isNotEmpty)
          const{
            value: emailValue,
            isValid: emailIsValid,
            valueChangeHandler: emailChangedHandler,
             inputBlurHandler: emailBlurHandler,
              reset: resetEmail
              } = useInput(isEmail)

  return (
    <form>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' value={firstNameValue} onChange={firstNameChangedHandler} onBlur={firstNameBlurHandler} />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' value={lastNameValue} onChange={LastnameChangedHandler} onBlur={LastNameBlurHandler} />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' value={emailValue} onChange={emailChangedHandler}  onBlur={emailBlurHandler} />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
