import {useState } from "react";



const SimpleInput = (props) => {


  const[enteredName, setEnteredName] = useState('')
const[enteredNameTouched, setEnteredNameTouched] = useState(false)

const enteredNameIsValidState = enteredName.trim() !=='';

const enteredEmailIsValid = enteredEmail.includes('@');
const enteredEmailIsInValid = !enteredEmailIsValid 

const nameInputIsInvalid = !enteredNameIsValidState && enteredNameTouched

const[enteredEmail, setEnteredEmail] = useState('')
const[enteredEmailTouhed, setEnteredEmailTouched] = useState(false)

let formIaValid = false


if(enteredNameIsValidState){
formIaValid=true
}

  const nameInputChangeHandler = event=>{
    setEnteredName(event.target.value)

  }
  const emailInputChangeHandler = event=>{
   setEnteredEmail(event.target.value)

  }


const nameInputBlurHandler = event =>{
setEnteredNameTouched(true)

}

const emailInputBlurHandler = event =>{
  setEnteredEmailTouched(true)

  }
const formSubmitHandler = event=>{
  event.preventDefault();
  setEnteredNameTouched(true)

if(!enteredNameIsValidState){
  return
}
  setEnteredName('')
  setEnteredNameTouched(false)

  setEnteredEmail('')
  setEnteredEmailTouched(false)
}


const nameInputClasses = nameInputIsInvalid? 'form-control invalid': 'form-control'

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input  type='text' id='name' onChange={nameInputChangeHandler} value={enteredName} onBlur={nameInputBlurHandler} />
        {nameInputIsInvalid && <p className="error-text">name must not be empty</p>}

      </div>
      <div className={nameInputClasses}>
        <label htmlFor='email'>Your E-MAIL</label>
        <input  type='email' id='email' onChange={emailInputChangeHandler} value={enteredEmail} onBlur={emailInputBlurHandler} />
        {nameInputIsInvalid && <p className="error-text">Please enter a valid text</p>}

      </div>
      <div className="form-actions">
        <button disabled={!formIaValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
